import $ from 'jquery';
import {jwtDecode} from 'jwt-decode';


const modulesUser = {
    state: {
        id : "",
        username : "",
        photo : "",
        followerCount : 0,
        access: "",
        refresh: "",
        is_login : false,
    },

    getters: {
    },

    mutations: {
    },

    actions: {
        login : (context, data) => {
            $.ajax({
                url : "https://app165.acapp.acwing.com.cn/api/token/",
                type : "POST",  
                data : {
                    username : data.username,
                    password : data.password,
                },
                success(resp) {
                    const {access} = resp;
                    const access_obj = jwtDecode(access);
                    $.ajax({
                        url: "https://app165.acapp.acwing.com.cn/myspace/getinfo/",
                        type: "GET",
                        data : {
                            user_id: access_obj.user_id,
                        },
                        headers: {
                            'Authorization' : "Bearer " + access,
                        },
                        success(resp) {
                            console.log(resp);
                        }
                    })
                }
            })
        }
    },

    modules: {
    }
}

export default modulesUser;
